def generate_vocabulary():
    """Generate comprehensive kanji vocabulary data"""

    vocabulary = {
        "totalWords": 0,
        "levels": {
            "N5": 0,
            "N4": 0,
            "N3": 0,
            "N2": 0,
            "N1": 0
        },
        "words": []
    }

    # Get vocabulary for each level
    all_levels = [
        ("N5", get_n5_vocab()),
        ("N4", get_n4_vocab()),
        ("N3", get_n3_vocab()),
        ("N2", get_n2_vocab()),
        ("N1", get_n1_vocab())
    ]

    id_counter = 1
    for level, vocab_list in all_levels:
        vocabulary["levels"][level] = len(vocab_list)

        for word_data in vocab_list:
            word_id = f"k{id_counter:04d}"
            word, reading, romaji, meaning, kanji_list, pos, example_jp, example_kr, freq = word_data

            kanji_breakdown = []
            for k in kanji_list:
                char, kor, kor_meaning, kor_reading = k
                kanji_breakdown.append({
                    "char": char,
                    "korean": kor,
                    "meaning": kor_meaning,
                    "reading": kor_reading
                })

            vocabulary["words"].append({
                "id": word_id,
                "word": word,
                "reading": reading,
                "romaji": romaji,
                "meaning": meaning,
                "kanji": kanji_breakdown,
                "partOfSpeech": pos,
                "example": {
                    "jp": example_jp,
                    "kr": example_kr
                },
                "level": level,
                "frequency": freq
            })
            id_counter += 1

        print(f"✅ Generated {len(vocab_list)} {level} vocabulary entries")

    vocabulary["totalWords"] = len(vocabulary["words"])
    return vocabulary


def main():
    print("🎮 Generating JLPT Kanji Vocabulary Data...")
    print("📚 Target: 2000+ words across all levels\n")

    vocabulary = generate_vocabulary()

    # Save to JSON
    output_path = Path(__file__).parent.parent / "data" / "kanji-vocabulary.json"
    output_path.parent.mkdir(parents=True, exist_ok=True)

    with open(output_path, 'w', encoding='utf-8') as f:
        json.dump(vocabulary, f, ensure_ascii=False, indent=2)

    print(f"\n✅ Vocabulary data generated: {output_path}")
    print(f"📊 Total words: {vocabulary['totalWords']}")
    print(f"📈 Breakdown:")

    for level in ["N5", "N4", "N3", "N2", "N1"]:
        count = vocabulary["levels"][level]
        print(f"   • {level}: {count} words")

    return 0


if __name__ == "__main__":
    exit(main())
